import React from "react";

const CalcFooter = () => {
    return (
        <div className='calcFooter'>
            <h2>How To Use</h2>
            <p>Select a <b>character</b> and <b>class</b> from the lists above.</p>
            <p>Click on the <b>Starsphere</b> and <b>Expertise</b> columns to toggle whether or not they are considered in the growth rates.</p>
        </div>
    )
}

export default CalcFooter;
