import React from "react";
import characters from "../json/Characters.json"
import classes from "../json/Classes.json"

const UnitSelect = ({
    character,
    setCharacter,
    gameClass,
    setGameClass
}) => {

    return (
        <div>
            <select defaultValue="None" onChange={(e) => setCharacter(e.target.value.name)}>
                {characters.map((character, index) => {
                    return (
                        <option key={index} value={character}>{character.name}</option>
                    )
                })}
            </select>
            <select defaultValue="None" onChange={(e) => setGameClass(e.target.value)}>
                {classes.map((gameClass, index) => {
                    return (
                        <option key={index} value={gameClass}>{gameClass.name}</option>
                    )
                })}
            </select>
        </div>
    )
}

export default UnitSelect;
