import React from "react";
import characters from "../json/Characters.json"
import classes from "../json/Classes.json"

const UnitSelect = ({
    setCharacter,
    setGameClass
}) => {

    return (
        <div>
            <select defaultValue={-1} onChange={(e) => setCharacter(characters[e.target.value])}>
                <option value={-1}>None</option>
                {characters.map((character, index) => {
                    return (
                        <option key={index} value={index}>{character.name}</option>
                    )
                })}
            </select>
            <select defaultValue={0} onChange={(e) => setGameClass(classes[e.target.value])}>
                {classes.map((gameClass, index) => {
                    return (
                        <option key={index} value={index}>{gameClass.name}</option>
                    )
                })}
            </select>
        </div>
    )
}

export default UnitSelect;
