import React, { useEffect, useState } from 'react';
import './App.css';
//my imports
import HeaderBar from './components/HeaderBar';
import GrowthTable from './components/GrowthTable';
import CharacterSelect from "./components/CharacterSelect"


function App() {
	const [character, setCharacter] = useState("");
    const [gameClass, setClass] = useState("")
    const [expertise, setExpertise] = useState(false);
    const [starsphere, setStarsphere] = useState(false);

	useEffect(() => {
		if(character && gameClass){
			document.title = `Growths for ${character.name} as ${gameClass.name}`
		} else {
			document.title = "Growths Calculator FE Engage"
		}
	}, [character, gameClass])



  	return (
    	<div className="App">
      		<header className="App-header">
        		<HeaderBar />
      		</header>
	      	<main>
				<CharacterSelect />
        		<GrowthTable character={character} gameClass={gameClass} expertise={expertise} starsphere={starsphere}/>
	    	</main>
    	</div>
  	);
}

export default App;
